#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\a_front-end\Why\11-TypeScript开发实战\TS\8.TS扩展-webpack\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/a_front-end/Why/11-TypeScript开发实战/TS/8.TS扩展-webpack/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../registry.npmmirror.com+uuid@8.3.2/node_modules/uuid/dist/bin/uuid" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../registry.npmmirror.com+uuid@8.3.2/node_modules/uuid/dist/bin/uuid" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../registry.npmmirror.com+uuid@8.3.2/node_modules/uuid/dist/bin/uuid" $args
  } else {
    & "node$exe"  "$basedir/../../../../../registry.npmmirror.com+uuid@8.3.2/node_modules/uuid/dist/bin/uuid" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
